format.formatContent:
{{#each ComponentRecordJson.ag-hero-section}}
<section class="uk-section uk-section-xlarge uk-background-cover uk-light uk-flex uk-flex-middle" 
         style="background-image: url('{{data.backgroundImage}}');" 
         data-component="hero-section">

  <div class="uk-container">
    <div class="uk-width-2-3@m uk-text-center uk-margin-auto">

      <h1 class="uk-heading-xlarge uk-margin-remove-top" %%componentRecordEditable%%>
        {{data.mainHeading}}
      </h1>

      <p class="uk-text-lead uk-margin-medium-top">
        {{data.subheading}}
      </p>

      <div class="uk-margin-large-top">
        {{#if data.primaryCtaLink}}
          <a href="{{data.primaryCtaLink}}" class="uk-button uk-button-primary uk-button-large uk-margin-small-right">
            {{data.primaryCtaText}}
          </a>
        {{else}}
          <button class="uk-button uk-button-primary uk-button-large uk-margin-small-right" type="button">
            {{data.primaryCtaText}}
          </button>
        {{/if}}

        {{#if data.secondaryCtaLink}}
          <a href="{{data.secondaryCtaLink}}" class="uk-button uk-button-default uk-button-large">
            {{data.secondaryCtaText}}
          </a>
        {{else}}
          <button class="uk-button uk-button-default uk-button-large" type="button">
            {{data.secondaryCtaText}}
          </button>
        {{/if}}
      </div>

    </div>
  </div>

</section>
{{/each}}
