ComponentFormat.FormatContent:
{{#each ComponentRecordJson.feature-left-image-with-awards}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding feature-left-img  {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} {{IfCond data.enable-background-image[].0 '=' 'Yes'}} bg-img {{/IfCond}}">
  <div class="intro-sectionbg">
    <img src="{{data.bg-image.0.OriginalImagePath}}" 
         alt="{{data.bg-image-alt}}" 
         width="1440" height="726" tabindex="0">
  </div>




  <div class="uk-container feature-leftimg__container snippet__container">
    {{#each Child.feature-image-2-items}}
    <div class="feature-overview">
      {{IfCond this.ParentId  '='  ../this.Id}}
      <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
        <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
          <div class="snippet__media feature-left_img__media uk-position-relative">
            <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="495" height="502" tabindex="0">
          </div>
        </div>
        <div class="uk-width-expand@l uk-width-1-2@m snippet__col">

          {{IfCond data.snippet-tagline 'notempty' ''}}
          <div class="small-title" tabindex="0">{{data.snippet-tagline}}</div>
          {{/IfCond}}

          {{IfCond data.consider-as-h1[].0 '=' 'Yes'}}
          <h1 id="main-content-h1" class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h1>
          {{else}}

          <h2 class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

          {{/IfCond}}

          {{IfCond data.snippet-description 'notempty' ''}}
          <div class="js-texttoggle">
            <div class="snippet__desc text-editor" tabindex="0">                        
              {{{data.snippet-description}}}
              {{IfCond data.snippet-description-more 'notempty' ''}}
              <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                {{{data.snippet-description-more}}}
              </div>
              {{/IfCond}}
            </div>
            {{IfCond data.snippet-description-more 'notempty' ''}}
            <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
              <span class="button-readmore__text button-readmore__text--more">Read More
                <span class="sr-only">for {{data.snippet-title}}</span>
              </span>
              <span class="button-readmore__text button-readmore__text--less">Less
                <span class="sr-only">for {{data.snippet-title}}</span>
              </span>
            </button>
            {{/IfCond}}
          </div>
          {{/IfCond}}


          {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
          <div class="snippet__actions">                        
            {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
            <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">{{data.snippet-primary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
            {{/IfCond}}                        
            {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
            <a href="{{data.snippet-secondary-button-link}}" class="uk-button uk-button-secondary">{{data.snippet-secondary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
            {{/IfCond}}
          </div>
          {{/IfMultipleOrCond}}
        </div>
      </div>
      {{/IfCond}}
    </div>

    <!-- Awards -->
    {{IfCond data.award-section[].0 '=' 'Yes'}}
    <div class="uk-container">
      <div class="home-awards-section">
        <div class="home-awards-heading">
          {{IfCond data.award-title 'notempty' ''}}
          <h2 class="homeawards__maintitle" tabindex="0">{{data.award-title}}</h2>
          {{/IfCond}}
        </div>

        <div class="feature-award-container">
          <div class="awardssliderlist uk-position-relative uk-visible-toggle" data-name="awardsslider" data-uk-scrollspy="cls: uk-animation-fade; target: .home-awards__grid; delay: 200;" data-uk-slider>


             <div class="uk-slider-items uk-grid">
            {{#each Child.feature-award-section}}

                 <div class="uk-width-4-5 uk-width-1-5@m home-awards__grid">
              <div class="home-awards__box">
                <span class="home-awards__icon dynamiccomponenteditenable" %%componentRecordEditable%%>
                  <img src="{{data.award-logo.0.OriginalImagePath}}" 
                       alt="{{data.award-logo-alt-text}}" 
                       width="{{data.width}}" 
                       height="{{data.height}}" 
                       tabindex="0">
                </span>
              </div>
            </div>
            {{/each}}
            </div>
          </div>
        </div>
      </div>
    </div>
    {{/IfCond}}

    {{/each}} 
  </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}
