ComponentFormat.FormatContent:
{{#each ComponentRecordJson.aspen-room}}

<div class="lt489-widget snippet {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
  <div class="uk-container snippet__container lt489-widget__container">

    {{IfCond data.title 'notempty' ''}}  
    <h2 class="title title--xl snippet__title lt489-widget__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
    {{/IfCond}}
    <div class="uk-grid snippet__zigzag snippet__zigzag--revert lt489-widget__zigzag gutter-xl" data-uk-grid>
      <div class="uk-width-expand@l uk-width-1-2@m snippet__col lt489-widget__col">
        <div class="lt489-widget__switcher" data-uk-switcher="connect: .switcher-container; active: 0">
          {{#each childRecords}}
          {{IfCond data.item-link 'notempty' ''}}
          <div class="lt489-widget__item">
            <a href="{{data.item-link}}">
              {{IfCond data.item-title 'notempty' ''}}
              <h2 class="title title--m lt489-widget__name dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">
                {{data.item-title}}  
              </h2>
              {{/IfCond}}
              {{IfCond data.item-description  'notempty' ''}}
              <div class="text-editor lt489-widget__text" tabindex="0">

                  {{{data.item-description}}}

              </div>
              {{/IfCond}}
            </a>
          </div>
          {{else}}
          <div class="lt489-widget__item">
            {{IfCond data.item-title 'notempty' ''}}
            <h2 class="title title--m lt489-widget__name" tabindex="0">
              {{data.item-title}}  
            </h2>
            {{/IfCond}}
            {{IfCond data.item-description  'notempty' ''}}
            <div class="text-editor lt489-widget__text" tabindex="0">

                {{{data.item-description}}}

            </div>
            {{/IfCond}}

          </div>
          {{/IfCond}}
          {{/each}}
        </div>
      </div>

      <div class="uk-width-auto@l uk-width-1-2@m snippet__col lt489-widget__col">
        <div class="lt194-widget__images uk-switcher switcher-container">
          {{#each childRecords}}
          <div class="snippet__media uk-position-relative lt194-widget__media">
            {{IfCond data.badge 'notempty' '' 'and' data.badge-alt 'notempty' ''}}
            <img src="{{data.badge.0.OriginalImagePath}}" alt="{{data.badge-alt}}" class="home-room__badge" width="104" height="124" tabindex="0">
            {{/IfCond}}
            <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.image-alt-text}}"
                 width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" tabindex="0"> 
          </div>
          {{/each}}
        </div>

      </div>
    </div>
  </div>
</div>
{{/each}}
