ComponentFormat.FormatContent:
{{#each ComponentRecordJson.home-dining-section}}
<div class="home-dining-sections" data-src="{{data.bg-image.0.OriginalImagePath}}" data-uk-img>
  <div class="uk-container " >
    <div class="home__dining-content" data-uk-scrollspy="cls: uk-animation-slide-bottom; target: .home__dining__heading; delay: 200;">
      <div class="small-title home__dining__tagline" tabindex="0">{{data.small-title}}</div>
      <h2 class="home__dining__heading dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>

      {{IfCond data.description 'notempty' ''}}
      <div class="home__dining__description" tabindex="0">
        {{{data.description}}}
      </div>
      {{/IfCond}}

      {{IfCond data.main-cta-text 'notempty' ''}}
      <div class="home__dining-action">
        <a href="{{data.main-cta-link}}" class="uk-button uk-button-secondary home__dining-link" role="button">
          {{data.main-cta-text}}
          <span class="sr-only">{{data.title}}</span>
        </a>
      </div>
      {{/IfCond}}
    </div>
    <div class="home__dining-plateimage">
      <img src="{{data.image-plate.0.OriginalImagePath}}" alt="{{data.image-plate-alt}}" class="home__dining__image" width="720" height="661" tabindex="0" data-uk-scrollspy="cls: uk-animation-fade uk-animation-15; delay: 500">
    </div>
  </div>
</div>
{{/each}}
