ComponentFormat.FormatContent:
 {{#each ComponentRecordJson.card-grid-with-title}}
<div class="lt523-widget inner-padding card-with-title
{{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
{{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
    <div class="uk-container">
        {{IfCond data.title 'notempty' ''}}
            <div class="snippet-grid__head" data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .card-title-animation; delay: 100; repeat: false">
                <h2 class="card-title-animation title title--xl" tabindex="0">{{data.title}}</h2>
            </div>
        {{/IfCond}}
        <div class="uk-grid snippet__grid gutter-xl" data-uk-grid data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .card-cont-animation; delay: 100; repeat: false">
            {{#each Child.card-list}}
                <div class="uk-width-1-2@m snippet-grid__col">
                    <div class="snippet-grid__media card-cont-animation">
                        <img src="{{data.image.0.OriginalImagePath}}" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" alt="{{data.image-alt-text}}" tabindex="0">
                    </div>
                    <div class="lt523-widget__col--content snippet-grid__content">
                        {{IfCond data.tagline 'notempty' ''}}
                            <div class="title--tagline snippet__smalltitle" tabindex="0">
                                {{data.tagline}}
                            </div>
                        {{/IfCond}}

                        <h3 class="card-cont-animation snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.card-title}}</h3>

                        {{IfCond data.short-description 'notempty' ''}}
                        <div class="card-cont-animation js-texttoggle">
                            <div class="snippet-grid__desc text-editor" tabindex="0">
                                {{{data.short-description}}}
                            </div>
                            {{IfCond data.description 'notempty' ''}}
                                <button type="button" class="uk-button button-link button-link-inverse js-texttoggle-action" tabindex="0">
                                    <span class="button-readmore__text button-readmore__text--more">
                                      Read More
                                      <span class="template-icon-chevron-right-thick reverse"></span>
                                    </span>
                                    <span class="button-readmore__text button-readmore__text--less">Less</span>
                                </button>
                            {{/IfCond}}
                        </div>
                        {{/IfCond}}

                        <div class="card-cont-animation snippet-grid__actions lt523-widget__action">
                            {{IfCond data.primary-button-text 'notempty' '' 'and' data.primary-button-link 'notempty' ''}}
                            <a href="{{data.primary-button-link}}" class="uk-button uk-button-primary" role="button">
                                {{data.primary-button-text}}
                                <span class="template-icon-chevron-right-thick reverse"></span>
                                <span class="sr-only">for about - {{data.card-title}}</span></a>
                            {{/IfCond}}
                            {{IfCond data.secondary-button-text 'notempty' '' 'and' data.secondary-button-link 'notempty' ''}}
                            <a href="{{data.secondary-button-link}}" class="uk-button button-link">
                                {{data.secondary-button-text}}
                                <span class="template-icon-chevron-right-thick reverse"></span>
                                <span class="sr-only">for about - {{data.card-title}}</span></a>
                            {{/IfCond}}
                        </div>
                    </div>
                </div>
            {{/each}}
        </div>
    </div>
</div>
{{/each}}
