ComponentFormat.FormatContent:
{{#each ComponentRecordJson.feature-right-image}}
<!-- Layout 1 Left Side image Starts-->
<div class="feature-padding lt1-widget feature-right-image
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} " data-uk-scrollspy="cls:addBG">
        <div class="feature-background-wrapper">
<img src="{{data.feature-bg-image.0.OriginalImagePath}}" alt="{{data.feature-bg-image-alt}}" width="{{data.feature-bg-image.0.ResourceWidth}}" height="{{data.feature-bg-image.0.ResourceHeight}}">
        </div>

    <div class="uk-container uk-container-small lt1-widget__container snippet__container">
    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xl margin-bottom-xl" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

        {{#each Child.feature-right-image-items}}
            {{IfCond this.ParentId  '='  ../this.Id}}
                <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag" data-uk-grid>
                    <div class="uk-width-1-1 uk-width-1-2@m snippet__col" data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .left-thumb-animation; delay: 100; repeat: false">

                        {{IfCond data.snippet-tagline 'notempty' ''}}
                            <div class="title--tagline snippet__smalltitle left-thumb-animation" tabindex="0">{{data.snippet-tagline}}</div>
                        {{/IfCond}}

                        <h1 id="main-content-h1" class="title title--xxl left-thumb-animation snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h1>

                        {{IfCond data.snippet-description 'notempty' ''}}
                        <div class="js-texttoggle">
                            <div class="snippet__desc text-editor left-thumb-animation" tabindex="0">
                                {{{data.snippet-description}}}
                                {{IfCond data.snippet-description-more 'notempty' ''}}
                                    <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                                        {{{data.snippet-description-more}}}
                                    </div>
                                {{/IfCond}}
                            </div>
                            {{IfCond data.snippet-description-more 'notempty' ''}}
                                <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
                                    <span class="button-readmore__text button-readmore__text--more">
                                        Read More
                                        <span class="sr-only">Read More for - {{data.snippet-title}}</span>
                                    </span>
                                    <span class="button-readmore__text button-readmore__text--less">
                                        Less
                                        <span class="sr-only">Less for - {{data.snippet-title}}</span>
                                    </span>
                                </button>
                            {{/IfCond}}
                        </div>
                        {{/IfCond}}


                        {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
                            <div class="snippet__actions left-thumb-animation">
                                {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
                                    <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">
                                        {{data.snippet-primary-button-text}}
                                        <span class="template-icon-chevron-right-thick reverse"></span>
                                        <span class="sr-only">for {{data.snippet-title}}</span></a>
                                {{/IfCond}}
                                {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
                                    <a href="{{data.snippet-secondary-button-link}}" class="uk-button button-link">
                                      {{data.snippet-secondary-button-text}}
                                      <span class="template-icon-chevron-right-thick reverse"></span>
                                      <span class="sr-only">for {{data.snippet-title}}</span>
                                    </a>
                                {{/IfCond}}
                            </div>
                        {{/IfMultipleOrCond}}
                    </div>
                    <div class="uk-width-1-1 uk-width-1-2@m snippet__col" data-uk-scrollspy="cls: uk-animation-slide-right-medium; repeat: false">

                        <div class="intro-media snippet__media uk-position-relative">

                        {{IfCondObj data.snippet-image '>' '1'  }}

                            <div id="intro-image-slider" class="uk-visible-toggle" tabindex="-1" data-uk-slideshow="animation: fade; autoplay: true; autoplay-interval: 5000;pause-on-hover: false">
                                <ul class="uk-slideshow-items uk-child-width-1-1">
                                    {{#each data.snippet-image}}
                                    <li class="snippet__media">
                                        <img src="{{OriginalImagePath}}" alt="{{../data.snippet-image-alt-text}}" width="530" height="607" tabindex="0">
                                    </li>
                                    {{/each}}
                                </ul>              
                                {{IfCondObj data.snippet-image '>' '1'  }}                 
                                <div class="uk-position-bottom-left uk-position-small">
                                  <button id="slideshow-toggle" class="uk-button uk-button-primary-inverse uk-button-small">
                                    <span class="play template-icon-play-circle"></span>
                                    <span class="pause template-icon-pause-circle"></span>
                                    <span class="sr-only">Slideshow play pause icon</span>
                                  </button>
                                </div>
                                {{/IfCondObj}}
                                <!-- {{IfCondObj data.snippet-image '>' '1'  }} 
                                <div class="slider-controls">
                                    <div class="slider-controls__arrows uk-flex uk-flex-middle uk-flex-between">
                                        <a class="slider-controls__slidenav slider-controls__slidenav--left uk-icon uk-slidenav-previous uk-slidenav" href="#" data-uk-slider-item="previous">
                                            <span class="template-icon template-icon-chevron-left-thin"></span>
                                            <span class="sr-only">Go to Previous Slide of {{data.section-title}}</span>
                                        </a>

                                        <a class="slider-controls__slidenav slider-controls__slidenav--right uk-icon uk-slidenav-next uk-slidenav" href="#" data-uk-slider-item="next">
                                            <span class="template-icon template-icon-chevron-right-thin"></span>
                                            <span class="sr-only">Go to Next Slide of {{data.section-title}}</span>
                                        </a>
                                    </div>
                                </div>
                                {{/IfCondObj}} -->
                            </div>


                        {{else}}
                            <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="530" height="607" tabindex="0">
                        {{/IfCondObj}}

                        </div>


                    </div>
                </div>
            {{/IfCond}}
        {{/each}}
    </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}
