ComponentFormat.FormatContent:
{{#each ComponentRecordJson.inside-page-zig-zag}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding lt1-widget inside-page-zig-zag
    {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
    {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
{{IfCond data.start-grid-image-from[].0 '=' 'Right'}} snippet__zigzag--revert {{/IfCond}}
    ">
    <div class="uk-container lt1-widget__container snippet__container" data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .top-section-animation; delay: 100; repeat: false">
		<div class="section-heading">
			{{IfCond data.section-tagline 'notempty' ''}}
				<div class="title title--tagline snippet__smalltitle top-section-animation" tabindex="0">{{data.section-tagline}}</div>
			{{/IfCond}}
			{{IfCond data.section-title 'notempty' ''}}
			<h2 class="title title--xxl margin-bottom-xl top-section-animation" tabindex="0">{{data.section-title}}</h2>
			{{/IfCond}}
		</div>
		<div class="inside-page-zig-zag-box gutter-xl">
			{{#each Child.inside-page-layout-zig-zag}}
			{{IfCond this.ParentId  '='  ../this.Id}}
			<div class="snippet__zigzag" data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .page-section-animation; delay: 100; repeat: false">
				<div class="snippet__col__box page-section-animation">
					<div class="snippet__media uk-position-relative">
						<img src="{{data.snippet-image-large.0.OriginalImagePath}}" alt="{{data.snippet-image-large-alt-text}}" width="690" height="570" tabindex="0">
					</div>
					<div class="snippet__col">
						<h2 class="title title--l snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>
						<div class="hover-card">
							{{IfCond data.snippet-description 'notempty' ''}}
							<div class="snippet__desc text-editor" tabindex="0">
								{{{data.snippet-description}}}
							</div>
							{{/IfCond}}
							<div class="snippet__actions">
								{{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
								<a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary uk-button-primary-inverse" data-autoDataLayer data-msevent="events" data-mscategory="cta clicks" data-msaction="click" onclick="pushToGtmRenderByDom(this);" ><span class="formslable" aria-hidden="true">{{data.snippet-primary-button-text}} - %%pagename%%</span>
									{{data.snippet-primary-button-text}}
									<span class="template-icon-chevron-right-thick reverse"></span>
									<span class="sr-only">for {{data.snippet-title}}</span>
								</a>
								{{/IfCond}}
							</div>
						</div>
					</div>
				</div>
			</div>
			{{/IfCond}}
			{{/each}}
		</div>
    </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}
