ComponentFormat.FormatContent:
{{#each ComponentRecordJson.3-col-grid-feature-card}}
<div class="inner-padding lt229-widget grid-card-with-design
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
  <div class="uk-container dynamiccomponenteditenable" %%componentRecordEditable%%>

    {{IfCond data.title 'notempty' ''}}
    <div class="snippet-grid__head">
      <h2 class="title title--xl lt229-widget__maintitle" tabindex="0">{{data.title}}</h2>
    </div>
    {{/IfCond}}
    <div class="lt229-widget__slider">
      <div class="uk-grid snippet-grid" data-uk-grid>
        {{#each childRecords}}
        <div class="lt229-widget__box grid-card uk-width-1-3@m uk-width-1-2@s">
          <div class="snippet-grid__box">
            <div class="snippet-grid__media">
              <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.alt-text}}" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" tabindex="0">
            </div>
            <div class="snippet-grid__content js-texttoggle dynamiccomponenteditenable" %%componentRecordEditable%%>
              {{IfCond data.tagline 'notempty' ''}}
                <div class="title--tagline">{{data.tagline}}</div>
              {{/IfCond}}

              <h3 class="title title--m lt229-widget__title" tabindex="0">{{data.title}}</h3>
              {{IfCond data.description 'notempty' ''}}
                <div class="text-editor" tabindex="0">
                    {{{data.description}}}
                    {{IfCond data.read-more-description 'notempty' ''}}
                    <div class="js-texttoggle-content" style="display: none;">
                        {{{data.read-more-description}}}
                    </div>
                    {{/IfCond}}
                </div>

                {{IfCond data.read-more-description 'notempty' ''}}
                    <button type="button" class="uk-button button-link button-readmore js-texttoggle-action">
                        <span class="button-readmore__text button-readmore__text--more">Read More</span>
                        <span class="button-readmore__text button-readmore__text--less">Less</span>
                    </button>
                {{/IfCond}}
              {{/IfCond}}

              {{IfCond data.primary-button-text 'notempty' '' 'or' data.secondary-button-text 'notempty' ''}}
              <div class="button-combo snippet-grid__actions">
                {{IfCond data.primary-button-text 'notempty' '' 'and' data.primary-button-link 'notempty' ''}}
                <a href="{{data.primary-button-link}}"
                   class="uk-button uk-button-primary button-booknow" role="button">
                    {{data.primary-button-text}}
                    <span class="template-icon-chevron-right-thick reverse"></span>
                   <span class="sr-only"> {{data.title}}</span></a>
                {{/IfCond}}
                {{IfCond data.secondary-button-text 'notempty' '' 'and' data.secondary-button-link 'notempty' ''}}
                <a href="{{data.secondary-button-link}}"
                   class="uk-button button-link">
                   {{data.secondary-button-text}}
                  <span class="template-icon-chevron-right-thick reverse"></span>
                   <span class="sr-only"> {{data.title}}</span>

                  </a>
                {{/IfCond}}
              </div>
              {{/IfCond}}

            </div>
          </div>
        </div>
        {{/each}}
      </div>
    </div>
  </div>
</div>
{{/each}}
