ComponentFormat.FormatContent:
{{#each ComponentRecordJson.section-layout-left-para}}
<!-- Layout 37 Starts -->
<div class="lt37-widget {{IfCond data.text-start-from-right[].0 '=' 'Yes'}} right-aligned-text {{/IfCond}}" data-src="{{data.background-image.0.OriginalImagePath}}" data-uk-img data-uk-parallax="bgy: -200">
    <div class="uk-container lt37-widget__container">
        <div class="lt37-widget__box">
            {{IfCond data.section-tag-line 'notempty' ''}}
            <div class="title title--tagline snippet__smalltitle" tabindex="0">{{data.section-tag-line}}</div>
            {{/IfCond}}
            <h2 class="title title--xl lt37-widget__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.section-title}}</h2>

            {{IfCond data.section-description 'notempty' ''}}
                <div class="text-editor lt37-widget__desc" tabindex="0">
                    {{{data.section-description}}}
                </div>
            {{/IfCond}}

            {{IfMultipleOrCond data.primary-button-link 'notempty' '' data.secondary-button-link 'notempty' ''}}   
                <div class="lt37-widget__action button-combo">
                    {{IfCond data.primary-button-link 'notempty' '' 'and' data.primary-button-text 'notempty' ''}}
                        <a href="{{data.primary-button-link}}" class="uk-button uk-button-default uk-button-default-inverse" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.primary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}} target="{{IfCond data.open-in-new-tab[].0 '=' 'Yes'}}_blank{{/IfCond}}">
                          <span class="formslable" aria-hidden="true">{{data.primary-button-text}} - {{data.section-title}}</span>
                          {{data.primary-button-text}} 
                          <span class="sr-only"> - {{data.section-title}}</span> </a>
                    {{/IfCond}}

                    {{IfCond data.secondary-button-link 'notempty' '' 'and' data.secondary-button-text 'notempty' ''}}
                        <a href="{{data.secondary-button-link}}" class="uk-button button-link button-link-inverse" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.snippet-secondary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}}>
                          <span class="formslable" aria-hidden="true">{{data.secondary-button-text}} - {{data.section-title}}</span>
                          {{data.secondary-button-text}} 
                          <span class="sr-only"> - {{data.section-title}}</span> 
                     </a>
                    {{/IfCond}}
                </div>
            {{/IfMultipleOrCond}}    
        </div>
    </div>

</div>
<!-- layout 37 Ends -->
{{/each}}
