ComponentFormat.FormatContent:
{{#each componentRecordJson.detail-snippet-right-align}}
<div class="inner-padding {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} detail-snippet--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} detail-snippet--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
<div class="offer-detail uk-container uk-container-1500" >
    <div class="uk-grid uk-child-width-1-2@m uk-child-width-1-1@s">
        <div class="offer-content-containter">
            <div class="offer-content">
               {{IfCond data.back-to-page-text 'notempty' '' 'and' data.back-to-page-link 'notempty' ''}}
              <a href="{{data.back-to-page-link}}" class="detail-widget__smalltitle back-button"><span class="template-icon-chevron-left"></span> {{data.back-to-page-text}}</a>
              {{/IfCond}}
                {{IfCond data.subtitle 'notempty' ''}}
                <div class="title title--tagline " tabindex="0" >{{data.subtitle}}</div>
                {{/IfCond}}
                <h2 class="offer-title dynamiccomponenteditenable" %%componentRecordEditable%%  tabindex="0">{{data.title}}</h2>
                {{IfCond data.description 'notempty' ''}}
              <div class="js-texttoggle">
                <div class="snippet__desc text-editor" tabindex="0">                        
                  {{{data.description}}}
                  {{IfCond data.detail-description 'notempty' ''}}
                  <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                    {{{data.detail-description}}}
                  </div>
                  {{/IfCond}}
                </div>
                {{IfCond data.detail-description 'notempty' ''}}
                <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
                  <span class="button-readmore__text button-readmore__text--more">Read More</span>
                  <span class="button-readmore__text button-readmore__text--less">Less</span>
                  <span class="sr-only"> {{data.title}}</span>
                </button>
                {{/IfCond}}
              </div>
              {{/IfCond}}
                {{IfCond data.cta-text 'notempty' '' 'and' data.cta-link 'notempty' ''}}
              <div class="snippet-action">
                <a href="{{data.cta-link}}" role="button" class="uk-button uk-button-primary " {{IfCond data.is-external[].0 '=' 'Yes'}}target="_blank"{{/IfCond}} data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.cta-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}}>
<span class="formslable" aria-hidden="true">{{data.cta-text}} - {{data.title}}</span> {{data.cta-text}} <span class="sr-only">for {{data.title}}</span></a>
              </div>
                {{/IfCond}}
            </div>
        </div>
        <div class="offer-snippet-image">

            <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.alt-text}}" width="700" height="525" tabindex="0" >

        </div>
    </div>
</div>
</div>
{{/each}}
