format.formatContent:
{{#each ComponentRecordJson.3-grid-with-hour}}
<div class="uk-section three-grid-with-hour
            {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} secondary-bg {{/IfCond}} {{/each}}
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}
            ">
  {{IfCond data.title 'notempty' ''}}
  <h2 class="uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
  {{/IfCond}}
  <div class="uk-container">
    <div class="uk-grid">
      {{#each childRecords}}
      <div class="uk-width-1-3 grid-list">
        <div class="grid-list-details">
          <h3 class="uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h3>
          {{IfCond data.description 'notempty' ''}}
          <div class="js-texttoggle">
            <div class="description text-editor" tabindex="0">                        
              {{{data.description}}}
            </div>          
          </div>
          {{/IfCond}}  

          {{#each childRecords}}
          <div class="hr-details uk-text-center">
              <div class="dynamiccomponenteditenable hr-title" %%componentRecordEditable%% tabindex="0">{{data.title}}</div>
              {{IfCond data.description 'notempty' ''}}
                <div class="description text-editor" tabindex="0">                        
                  {{{data.description}}}
                </div>
              {{/IfCond}}  
          </div>
          {{/each}}

          {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
              <div class="button-combo"> 
                <a href="{{data.button-link}}" class="uk-button uk-button-primary" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}} target="{{IfCond data.open-in-new-tab[].0 '=' 'Yes'}}_blank{{/IfCond}}"> <span class="formslable" aria-hidden="true">{{data.button-text}} - {{data.title}}</span> {{data.button-text}} <span class="sr-only">for {{data.title}}</span></a>
              </div>
           {{/IfCond}}  
        </div>
      </div>
      {{/each}}
    </div>
  </div>
</div>
{{/each}}

{{#each ComponentRecordJson.landing-card-links}}
<div class="uk-section landing-card-links            
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}
            ">
  <div class="uk-container">
  	 {{IfCond data.title 'notempty' ''}}
      <h2 class="uk-h3 uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
     {{/IfCond}}
     <div class="uk-grid">
    	{{#each childRecords}}
          <div class="uk-width-1-2 card-links-list dynamiccomponenteditenable" %%componentRecordEditable%% >
            <a href="{{data.link}}" class="uk-button uk-button-primary card-links" target="{{IfCond data.open-in-new-tab[].0 '=' 'Yes'}}_blank{{/IfCond}}">{{data.link-text}}<span class="sr-only">Link to {{data.link-text}}</span></a>
          </div>
        {{/each}}
     </div>
  </div>
</div>
{{/each}}

{{#each ComponentRecordJson.3-grid-with-bullet}}
<div class="uk-section three-grid-with-bullet
            {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} secondary-bg {{/IfCond}} {{/each}}
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}
            ">
  {{IfCond data.title 'notempty' ''}}
  <h2 class="uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
  {{/IfCond}}
  <div class="uk-container">
    <div class="uk-grid">
      {{#each childRecords}}
      <div class="uk-width-1-3 grid-list">
        <div class="grid-list-details">
            <h3 class="uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h3>
            {{IfCond data.description 'notempty' ''}}
            <div class="js-texttoggle">
              <div class="description text-editor" tabindex="0">                        
                {{{data.description}}}
              </div>          
            </div>
            {{/IfCond}}
        </div>
      </div>
      {{/each}}
    </div>
  </div>
</div>
{{/each}}

{{#each ComponentRecordJson.section-layout-with-slider}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding lt212slider-widget js-lt212slider-widget  
        {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} lt212-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} lt212-widget--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
    ">


    <div class="uk-container lt212slider-widget__container snippet__container dynamiccomponenteditenable" %%componentRecordEditable%%>

    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xl margin-bottom-xl lt212slider-widget__title js-lt212slider-widget-main-title" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

        <div class="snippet uk-slider uk-slider--fade uk-position-relative lt212slider-widget__slider" data-uk-slider="clsActivated: uk-transition-active; velocity: 10">

            <div class="uk-slider-container">
                <div class="uk-slider-items uk-child-width-1-1">

                    {{#each Child.section-layout-with-dots-slider-items1}}
                        {{IfCond this.ParentId  '='  ../this.Id}}
                        <div class="uk-transition-fade lt212slider-widget__item dynamiccomponenteditenable" %%componentRecordEditable%%>
                            <div class="uk-grid gutter-xl lt212slider-widget__grid" data-uk-grid>
                                <div class="uk-width-auto@l uk-width-1-2@m snippet__col lt212slider-widget__col lt212slider-widget__col--media">
                                    <div class="snippet__media uk-position-relative">
                                        <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="616" height="640" tabindex="0">
                                    </div>
                                </div>
                                <div class="uk-width-expand@l uk-width-1-2@m snippet__col lt212slider-widget__col  lt212slider-widget__col--content js-lt212slider-widget-content-ele">

                                    {{IfCond data.snippet-tagline 'notempty' ''}}
                                        <div class="title title--tagline snippet__smalltitle" tabindex="0">{{data.snippet-tagline}}</div>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-title 'notempty' ''}}
                                    <h2 class="title title--l snippet__title" tabindex="0">{{data.snippet-title}}</h2>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-sub-title 'notempty' ''}}
                                    <h2 class="title title--s snippet__title" tabindex="0">{{data.snippet-sub-title }}</h2>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-description 'notempty' ''}}
                                    <div class="js-texttoggle">
                                        <div class="snippet__desc text-editor" tabindex="0">                        
                                            {{{data.snippet-description}}}
                                            {{IfCond data.snippet-description-more 'notempty' ''}}
                                                <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                                                    {{{data.snippet-description-more}}}
                                                </div>
                                            {{/IfCond}}
                                        </div>
                                        {{IfCond data.snippet-description-more 'notempty' ''}}
                                            <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
                                                <span class="button-readmore__text button-readmore__text--more">Read More</span>
                                                <span class="button-readmore__text button-readmore__text--less">Less</span>
                                              <span class="sr-only">{{data.snippet-title}}</span>
                                            </button>
                                        {{/IfCond}}
                                    </div>
                                    {{/IfCond}}

                                    {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
                                        <div class="snippet__actions">                        
                                            {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
                                                <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.snippet-primary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}}>
         <span class="formslable" aria-hidden="true">{{data.snippet-primary-button-text}} - {{data.snippet-title}}</span> 
                                                  {{data.snippet-primary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
                                            {{/IfCond}}                        
                                            {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
                                                <a href="{{data.snippet-secondary-button-link}}" class="uk-button button-link">{{data.snippet-secondary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
                                            {{/IfCond}}
                                        </div>
                                    {{/IfMultipleOrCond}}
                                </div>
                            </div>
                        </div>
                        {{/IfCond}}
                    {{/each}}

                </div>
              	<div class="lt212slider-widget__dotnav">
              		<a class="uk-position-center-left uk-position-small" href="#" uk-slidenav-previous uk-slider-item="previous"></a>
                    <a class="uk-position-center-right uk-position-small" href="#" uk-slidenav-next uk-slider-item="next"></a>
              	</div>
            </div>
        </div>
    </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.zigzag-snippet-with-tabs}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding lt1-widget zigzag-snippet-with-tabs
        {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} lt1-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} lt1-widget--secondary-bg {{/IfCond}} {{IfCond this '=' 'Gradient Color'}} lt1-widget--gradient-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} {{IfCond data.alternate-snippet-background-color[].0 '=' 'Yes'}} alternate-bg-color {{/IfCond}} " data-uk-filter="target: > .js-snippet-filter">

    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xxl margin-bottom-xl uk-text-center zig-zag-tabs-title" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

    <div class="uk-container tab-list__container margin-bottom-xl">
        <ul class="tab-list tab-list--center tab-list--mobscrollable room-types__tabs" >

           {{IfCond data.all-option-tab-hide[].0 '=' 'Yes'}}

                {{#each Child.venue-right-image-with-tabs-categories1}}
                    {{IfCond this.ParentId  '='  ../this.Id}}
                        <li class="tab-list__item dynamiccomponenteditenable {{IfCond @index '=' '0'}} uk-active {{/IfCond}}" data-uk-filter-control="[data-category='{{data.category-name}}']" data-roomtypetab="{{data.category-name}}" %%componentRecordEditable%%><a class="tab-list__link" href="#1">{{data.category-name}}</a></li>
                    {{/IfCond}}
                {{/each}}

            {{else}}          
                <li class="tab-list__item uk-active" data-uk-filter-control data-roomtypetab="{{data.category-name}}"><a class="tab-list__link" href="javascript:;">All Options</a></li>
                {{#each Child.venue-right-image-with-tabs-categories1}}
                    {{IfCond this.ParentId  '='  ../this.Id}}
                        <li class="tab-list__item dynamiccomponenteditenable" data-uk-filter-control="[data-category='{{data.category-name}}']" data-roomtypetab="{{data.category-name}}" %%componentRecordEditable%%><a class="tab-list__link" href="#1">{{data.category-name}}</a></li>
                    {{/IfCond}}
                {{/each}}         
            {{/IfCond}}          

        </ul>      
    </div>

    <div class="lt1-widget__container snippet__container js-snippet-filter zig-zag-container">
        {{#each Child.venue-right-image-with-tabs-categories1}}
            {{IfCond this.ParentId  '='  ../this.Id}}
                {{#each Child.venue-right-image-with-tabs-snippets1}}
                    {{IfCond this.ParentId  '='  ../this.Id}}
                        <div class="full-width-sec uk-flex-middle snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}} js-snippet-filter-item" data-category='{{../../data.category-name}}'>
                            <div class="uk-container ">
      <div class="uk-grid gutter-xl" data-uk-grid>
                            <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
                                <div class="snippet-media uk-position-relative">
                                    <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="616" height="502" tabindex="0" class="snippet-image">
                                </div>
                            </div>
                            <div class="uk-width-expand@l uk-width-1-2@m snippet__col snippet-content">
							  <div class="uk-content-panel">
                                {{IfCond data.snippet-tagline 'notempty' ''}}
                                    <div class="title title--tagline snippet__smalltitle" tabindex="0">{{data.snippet-tagline}}</div>
                                {{/IfCond}}

                                <h2 class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>


                                {{IfCond data.snippet-description 'notempty' ''}}
                                <div class="js-texttoggle">
                                    <div class="snippet__desc text-editor" tabindex="0">                        
                                        {{{data.snippet-description}}}
                                        {{IfCond data.snippet-description-more 'notempty' ''}}
                                            <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                                                {{{data.snippet-description-more}}}
                                            </div>
                                        {{/IfCond}}
                                    </div>
                                    {{IfCond data.snippet-description-more 'notempty' ''}}
                                        <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
                                            <span class="button-readmore__text button-readmore__text--more">Read More</span>
                                            <span class="button-readmore__text button-readmore__text--less">Less</span>
                                          <span class="sr-only">{{data.section-title}}</span>
                                        </button>
                                    {{/IfCond}}
                                </div>
                                {{/IfCond}}


                                {{IfMultipleOrCond data.button-link 'notempty' '' data.secondary-button-link 'notempty' ''}}   
                      	<div class="button-combo">
             {{IfCond data.button-text 'notempty' '' 'and' data.button-link 'notempty' ''}}
                        <a href="{{data.button-link}}" class="uk-button uk-button-primary test" data-autodatalayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" 
                 {{IfMultipleOrCond 
                  (CheckWord "book" data.button-text) '=' 'True'}}
                  data-msevent="booking buttons" data-mscategory="booking entrances"
                  {{else}} 
                           data-msevent="events" data-mscategory="cta click" 
                  {{/IfMultipleOrCond}}  role="button" target="{{IfCond data.open-in-new-tab[].0 '=' 'Yes'}}_blank{{/IfCond}}">
                            <span class="formslable" aria-hidden="true">{{data.button-text}} - {{data.title}}</span>
                            {{data.button-text}}    
                            <span class="sr-only">for {{data.title}}</span>
                        </a>
             {{/IfCond}}
             {{IfCond data.secondary-button-text 'notempty' '' 'and' data.secondary-button-link 'notempty' ''}}
                        <a href="{{data.secondary-button-link}}" class="uk-button button-link" data-autodatalayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.secondary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}}  role="button" target="{{IfCond data.open-in-new-tab-secondary[].0 '=' 'Yes'}}_blank{{/IfCond}}">
                            <span class="formslable" aria-hidden="true">{{data.secondary-button-text}} - {{data.title}}</span>
                            {{data.secondary-button-text}}    
                            <span class="sr-only">{{data.secondary-button-text}} - {{data.title}}</span>
                        </a>
             {{/IfCond}}
                      	</div>
                      {{/IfMultipleOrCond}}


                              </div>
                            </div>
                              </div>
                          </div>

                        </div> 
                    {{/IfCond}}
                {{/each}}
            {{/IfCond}}

      		{{IfCond data.show-capacity-chart[].0 '=' 'Yes'}}
      		<div class="capacity-chart" data-category='{{../data.category-name}}'>
            {{#each Child.capacity-chart}}
                  <div class="uk-overflow-auto table5-col__data" tabindex="0">
                       <table>
                         <tr>
                           <th tabindex="0">{{data.room-title-text}}</th>
                            <th tabindex="0">{{data.sq-ft}}</th>
                            <th tabindex="0">{{data.height}}</th>
                            <th tabindex="0">{{data.width}}</th>
                            <th tabindex="0">{{data.length}}</th>
                            <th tabindex="0">{{data.classroom-2-6}}</th>
                            <th tabindex="0">{{data.classroom-3-6}}</th>
                            <th tabindex="0">{{data.hollow-square}}</th>
                            <th tabindex="0">{{data.u-shape}}</th>
                            <th tabindex="0">{{data.conference}}</th>
                            <th tabindex="0">{{data.theater-reception}}</th>
                            <th tabindex="0">{{data.banquet-rnd-10}}</th>
                            <th tabindex="0">{{data.exhibit-table-tops}}</th>
                            <th tabindex="0">{{data.exhibit-8x10}}</th>
                         </tr>

                         {{#each childRecords}}                         
                         <tr>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.room-title}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.sq-ft}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.height}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.width}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.length}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.classroom-2-6}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.classroom-3-6}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.hollow-square}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.u-shape}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.conference}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.theater-reception}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.banquet-rnd-10}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.exhibit-table-tops}}</td>
                            <td class="dynamiccomponenteditenable contentfocus" %%componentRecordEditable%%  tabindex="0">{{data.exhibit-8x10}}</td>
                         </tr>                         
                         {{/each}}

                      </table>
                  </div>

            {{/each}}
            </div>
      {{/IfCond}}

        {{/each}}


    </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.snippet-with-bg}}
<div class="uk-section snippet-right-image snippet-right-image-with-bg">
  	<div class="snippet-right-image-with-bg-inner">
        <div class="uk-container uk-container-large">
            <div class="uk-grid uk-child-width-1-2@m uk-flex-middle" data-uk-grid>
                <div class="snippet-right-content">
                    <div class="uk-card">
                        <div class="uk-card-body">
                            {{IfCond data.section-tagline 'notempty' ''}}
                            	<div class="title--tagline" tabindex="0">{{data.section-tagline}}</div>
                            {{/IfCond}}
                            <h2 class="dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
                            {{IfCond data.description 'notempty' ''}}
                              <div class="description" tabindex="0">{{{data.description}}}</div>
                            {{/IfCond}}         


  {{IfMultipleOrCond data.button-link 'notempty' '' data.secondary-button-link 'notempty' ''}}     
        <div class="snippet__actions"> 

          {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
          <a href="{{data.button-link}}" class="uk-button uk-button-primary uk-button-primary-inverse" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}} role="button" target="{{IfCond data.open-in-new-tab[].0 '=' 'Yes'}}_blank{{/IfCond}}"> <span class="formslable" aria-hidden="true">{{data.button-text}} - {{data.title}}</span> {{data.button-text}} <span class="sr-only">for {{data.title}}</span></a>
          {{/IfCond}}  

          {{IfCond data.secondary-button-link 'notempty' '' 'and' data.secondary-button-text 'notempty' ''}}
          <a href="{{data.secondary-button-link}}" class="uk-button button-link button-link-inverse" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.secondary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}} role="button" target="{{IfCond data.open-in-new-tab-secondary[].0 '=' 'Yes'}}_blank{{/IfCond}}">
            <span class="formslable" aria-hidden="true">{{data.secondary-button-text}} - {{data.title}}</span>            
            {{data.secondary-button-text}}
            <span class="sr-only">for {{data.title}}</span>
          </a>
          {{/IfCond}}
        </div>
        {{/IfMultipleOrCond}}     


                        </div>
                    </div>
                </div>
                <div class="snippet-right-media">
                    <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.alt-text}}" width="626" height="502" tabindex="0">                   
                </div>
            </div>
        </div>
    </div>
</div>
{{/each}}

{{#each ComponentRecordJson.contact-enquires}}
<div class="contacts-by-email">
    <div class="uk-container">
        <h2 class="uk-text-center dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.title}}</h2>
       {{IfCond data.description 'notempty' ''}}
              <div class="uk-text-center description" tabindex="0">{{{data.description}}}</div>
       {{/IfCond}}
        <div class="email-links">
            <ul>
              {{#each childRecords}}
                <li class="dynamiccomponenteditenable" %%componentRecordEditable%%>
                    <p class="snippet-feature-title" tabindex="0">{{data.title}}</p> <a href="mailto:{{data.mail}}" target="_blank">{{data.mail}}</a> </li>
              {{/each}}
            </ul>
        </div>
    </div>
</div>
{{/each}}

{{#each ComponentRecordJson.accessibility-features}}
<div class="uk-section accessibility-features  
            {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} secondary-bg {{/IfCond}} {{/each}}
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
            ">
        <div class="uk-container">
