                          <span class="sr-only"> - {{data.section-title}}</span> </a>
                    {{/IfCond}}

                    {{IfCond data.secondary-button-link 'notempty' '' 'and' data.secondary-button-text 'notempty' ''}}
                        <a href="{{data.secondary-button-link}}" class="uk-button button-link button-link-inverse" data-autoDataLayer onclick="pushToGtmRenderByDom(this)" data-msaction="click" {{IfCond (CheckWord "book" data.snippet-secondary-button-text) '=' 'True'}} data-msevent="booking buttons" data-mscategory="booking entrances" {{else}} data-msevent="events" data-mscategory="cta click" {{/IfCond}}>
                          <span class="formslable" aria-hidden="true">{{data.secondary-button-text}} - {{data.section-title}}</span>
                          {{data.secondary-button-text}} 
                          <span class="sr-only"> - {{data.section-title}}</span> 
                     </a>
                    {{/IfCond}}
                </div>
            {{/IfMultipleOrCond}}    
        </div>
    </div>

</div>
<!-- layout 37 Ends -->
{{/each}}
