format.formatContent:
{{#each ComponentRecordJson.feature-left-image-2}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding feature-left2 
        {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} feature-left2--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} feature-left2--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
    ">

    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xxl margin-bottom-xl" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

    <div class="uk-container feature-left2__container snippet__container" data-uk-scrollspy="cls: uk-animation-slide-bottom-medium ; target: .zig-left-animation; delay: 100; repeat: false">
        {{#each Child.feature-left-image-2-items}}
            {{IfCond this.ParentId  '='  ../this.Id}}
                <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
                    <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
                        <div class="snippet__media feature-left2__media uk-position-relative zig-left-animation">
                            <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="{{data.snippet-image.0.ResourceWidth}}" height="{{data.snippet-image.0.ResourceHeight}}" tabindex="0">
                        </div>
                    </div>
                    <div class="uk-width-expand@l uk-width-1-2@m snippet__col">

                        {{IfCond data.snippet-tagline 'notempty' ''}}
                            <div class="title title--tagline zig-left-animation snippet__smalltitle" tabindex="0">{{data.snippet-tagline}}</div>
                        {{/IfCond}}

                        <h2 class="title title--xl zig-left-animation snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

                        {{IfCond data.snippet-description 'notempty' ''}}
                        <div class="js-texttoggle">
                            <div class="snippet__desc text-editor zig-left-animation" tabindex="0">                        
                                {{{data.snippet-description}}}
                                {{IfCond data.snippet-description-more 'notempty' ''}}
                                    <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                                        {{{data.snippet-description-more}}}
                                    </div>
                                {{/IfCond}}
                            </div>
                            {{IfCond data.snippet-description-more 'notempty' ''}}
                                <button type="button" class="uk-button button-link button-link-inverse js-texttoggle-action" tabindex="0">
                                    <span class="button-readmore__text button-readmore__text--more">
                                      Read More
                                      <span class="template-icon-chevron-right-thick reverse"></span>
                                    </span>
                                    <span class="button-readmore__text button-readmore__text--less">Less</span>
                                </button>
                            {{/IfCond}}
                        </div>
                        {{/IfCond}}


                        {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
                            <div class="snippet__actions zig-left-animation">                        
                                {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
                                    <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary uk-button-primary-inverse" role="button" data-autoDataLayer data-msevent="events" data-mscategory="cta clicks" data-msaction="click" onclick="pushToGtmRenderByDom(this);" ><span class="formslable" aria-hidden="true">{{data.snippet-primary-button-text}} - %%pagename%%</span>
                                      {{data.snippet-primary-button-text}}
                                      <span class="template-icon-chevron-right-thick reverse"></span>
                                      <span class="sr-only">for {{data.snippet-title}}</span>
                                    </a>
                                {{/IfCond}}                        
                                {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
                                    <a href="{{data.snippet-secondary-button-link}}" class="uk-button button-link button-link-inverse" data-autoDataLayer data-msevent="events" data-mscategory="cta clicks" data-msaction="click" onclick="pushToGtmRenderByDom(this);" ><span class="formslable" aria-hidden="true">{{data.snippet-secondary-button-text}} - %%pagename%%</span>
                                      {{data.snippet-secondary-button-text}}
                                      <span class="template-icon-chevron-right-thick reverse"></span>
                                      <span class="sr-only">for {{data.snippet-title}}</span>
                                    </a>
                                {{/IfCond}}
                            </div>
                        {{/IfMultipleOrCond}}
                    </div>
                </div>
            {{/IfCond}}
        {{/each}}
    </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.aspen-blog}}
<div class="blg41-widget {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
                <div class="uk-container blg41-widget__container">
                    <div class="uk-grid blg41-widget__grid" data-uk-grid>
                        <div class="uk-width-expand blg41-widget__col blg41-widget__col--media">
                            <div class="uk-grid blg41-widget__cards" data-uk-grid>

                              {{#each childRecords}}
                                <div class="uk-width-1-2@s blg41-widget__item">
                                 <div class="blg41-widget__media">
                      <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.image-alt-text}}"
width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" tabindex="0"> 
							     </div>
                                    <div class="blg41-widget__text">
                                      {{IfCond data.title 'notempty' ''}}
                                        <h3 class="title title--s blg41-widget__name" tabindex="0">{{data.title}}</h3>
                                       {{/IfCond}}
                                        {{IfCond data.section-description 'notempty' ''}}
                                        <div class="text-editor blg41-widget__text" tabindex="0">
                                            <p>
                                              {{{data.section-description}}}
                                            </p>
                                        </div>
                                       {{/IfCond}}
                                        {{IfCond data.miles 'notempty' ''}}
                                        <div class="blg41-widget__icontext" tabindex="0">
                                            <span class="template-icon template-icon-bell-02 uk-margin-small-right"></span>
                                           {{data.miles}}
                                        </div>
                                        {{/IfCond}}
                                    </div>
                                </div>
                               {{/each}}


                            </div>
                        </div>
                        <div class="uk-width-auto blg41-widget__col blg41-widget__col--text">
                            <div class="blg41-widget__box">
                              {{IfCond data.tagline 'notempty' ''}}
                                <h2 class="title title--tagline blg41-widget__tagline" tabindex="0"> {{data.tagline}}</h2>
                               {{/IfCond}}
                                {{IfCond data.section-title 'notempty' ''}}
                                <h2 class="title title--l blg41-widget__title" tabindex="0">
                                 {{data.section-title}}
                                </h2>
                               {{/IfCond}}
                               {{IfCond data.description 'notempty' ''}}
                                <div class="text-editor blg41-widget__desc" tabindex="0">
                                    <p>
                                      {{{data.description}}}
                                    </p>
                                </div>
                                {{/IfCond}}
                            {{IfCond data.primary-button-text  'notempty' '' 'and' data.primary-button-link 'notempty' ''}}
                       <div class="blg41-widget__action">
                         <a href="{{data.primary-button-link}}" class="uk-button uk-button-primary">							{{data.primary-button-text}}
                                      <span class="sr-only">for {{data.section-title}}</span> 
                                  </a>
                                </div>
                                {{/IfCond}}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
{{/each}}

{{#each ComponentRecordJson.redwood-blog}}
<div class="bl-block snippet {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} bl-block--primary {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} bl-block--secondary {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">

    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xxl margin-bottom-xl" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

    <div class="uk-container snippet__container bl-block__container">
        {{#each Child.redwood-blog-items}}
            {{IfCond this.ParentId  '='  ../this.Id}}
                <div class="uk-grid gutter-xxl uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}  bl-block__grid" data-uk-grid>
                    <div class="uk-width-auto@l uk-width-1-2@m snippet__col bl-block__col">
                        <div class="snippet__media uk-position-relative bl-block__media">
                            <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="525" height="537" tabindex="0">
                        </div>
                    </div>
                    <div class="uk-width-expand@l uk-width-1-2@m snippet__col bl-block__col">
                        <h2 class="title title--xl snippet__title  bl-block__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-main-title}}</h2>

                        {{IfCond data.snippet-sub-title 'notempty' ''}}
                            <h3 class="title title--s bl-block__date" tabindex="0">{{data.snippet-sub-title}}</h3>
                        {{/IfCond}}

                        {{IfCond data.snippet-title 'notempty' ''}}
                            <h3 class="title title--l bl-block__name" tabindex="0">{{data.snippet-title}}</h3>
                        {{/IfCond}}

                        {{IfCond data.snippet-description 'notempty' ''}}
                            <div class="snippet__desc bl-block__desc text-editor" tabindex="0">
                                {{{data.snippet-description}}}
                            </div>
                        {{/IfCond}}

                        {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
                        <div class="snippet__actions bl-block__actions">
                            <a href="{{data.button-link}}" class="uk-button uk-button-primary">{{data.button-text}} <span class="sr-only"> - {{data.snippet-main-title}}</span> </a>
                        </div>
                        {{/IfCond}}
                    </div>
                </div>
            {{/IfCond}}
        {{/each}}
    </div>
</div>
{{/each}}

{{#each ComponentRecordJson.custom-html-snippet}}
<div class="inner-padding 
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
    ">
  <div class="text-editor uk-margin-remove" tabindex="0">{{{data.snippet-code}}}</div>
</div>
{{/each}}

{{#each ComponentRecordJson.gdpr-based-privacy-policy}}
<div class="inner-padding
    {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
    {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
  <div class="uk-container">
    {{IfCond data.section-title  'notempty' ''}}
    <h2 class="title title--l" tabindex="0">{{data.section-title }}</h2>
    {{/IfCond}}
    <div id="main-content-h1" class="text-editor gdpr-sec" tabindex="0">%%gdpr_privacy%%</div>
  </div>
</div>
{{/each}}

{{#each ComponentRecordJson.venue-accordian}}
<div class="inner-padding venue-accordian {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
    <div class="uk-container snippet__container">

        <div class="uk-grid gutter-xl snippet__zigzag snippet__zigzag--fullwidth dynamiccomponenteditenable" %%componentRecordEditable%%>
            <div class="uk-width-auto@m snippet__col">
                <div class="snippet__media">
                    <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.alt-text}}" width="610" height="460" data-name="contactnearbyboxes-image">
                </div>
            </div>
            <div class="uk-width-expand@m snippet__col">
                {{IfCond data.title 'notempty' ''}}
                <h2 class="snippet__title" tabindex="0" data-name="contactnearbyboxes-title">{{data.title}}</h2>
                {{/IfCond}}

                {{IfCond data.description 'notempty' ''}}
                <div class="snippet__desc" tabindex="0" data-name="contactnearbyboxes-description">
                    {{{data.description}}}
                </div>
                {{/IfCond}}

                {{IfCondObj Child.accordian-list-venue '>' '0'  }} 
                <ul class="accoradian snippet__accoradian snippet__accoradian--nearby-boxes" data-uk-accordion data-name="contactnearbyboxes-accordion">
                    {{#each child.accordian-list-venue}}
                  {{IfCond this.ParentId  '='  ../this.Id}}
                    <li class="dynamiccomponenteditenable" %%componentRecordEditable%%>
                        <a class="uk-accordion-title uk-accordion-title--spaservices" href="#">{{data.title}}</a>
                        <div class="uk-accordion-content uk-accordion-content--spaservices" tabindex="0" hidden>{{{data.description}}}</div>
                    </li>
                    {{/IfCond}}
                    {{/each}}
                </ul>
                {{/IfCondObj}}

                {{IfCond data.maincta-text 'notempty' '' 'and' data.maincta-link 'notempty' ''}}
                <div class="snippet__actions"> 
                    <a role="button" target="_blank" href="{{data.maincta-link}}" class="uk-button uk-button-primary" data-name="contactnearbyboxes-cta">{{data.maincta-text}} <span class="sr-only">{{data.title}}</span></a>
                </div>
                {{/IfCond}}
            </div>
        </div>

    </div>
</div>
{{/each}}

{{#each ComponentRecordJson.accessibility-dropdown}}
<!-- FAQ 3 Starts -->
<div class="faq3-widget {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} faq3-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} faq3-widget--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
    <div class="uk-container faq3-widget__container">
        <div class="faq3-widget__grid" >
            <div class="faq3-widget-content__col">
                {{IfCond data.section-tagline 'notempty' ''}}
                <div class="title title--tagline faq3-widget__smalltitle" tabindex="0">{{data.section-tagline}}</div>
                {{/IfCond}}
                {{IfCond data.section-title 'notempty' ''}}<h2 class="title title--xl faq3-widget__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.section-title}}</h2>{{/IfCond}}

                {{IfCond data.section-description 'notempty' ''}}
                <div class="text-editor faq3-widget__text" tabindex="0">{{{data.section-description}}}</div>
                {{/IfCond}}

                {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
                <div class="faq3-widget__action">
                    <a href="{{data.button-link}}" class="uk-button uk-button-primary ">{{data.button-text}} <span class="sr-only"> - {{data.section-title}}</span> </a>
                </div>
                {{/IfCond}}

            </div>
            <div class="faq3-widget-accordion__col faq3-widget__col--right">
                <div class="faq3-widget__content1 accessibility-content--accordion">
                    {{IfCondObj Child.accessibility-dropdown-items '>' '0'  }}
                    <div class="uk-accordion faq3-widget__accordion" data-uk-accordion>
                        {{#each Child.accessibility-dropdown-items}}
                        <div class="dynamiccomponenteditenable" %%componentRecordEditable%%>
                        <a class="uk-accordion-title" href="#">{{IfCond data.icon-text 'notempty' ''}}<span class="acc-icon-text {{data.icon-text}}"> </span>{{/IfCond}}{{data.title}}</a>
                            <div class="uk-accordion-content" tabindex="0" hidden>
                                {{{data.description}}}
                                {{IfCond data.show-gdpr-accessibility-text[].0 '=' 'Yes'}}
                                    <div class="text-editor gdpr-sec">
                                    %%gdpr_ada-website-conformance%%
                                    </div>
                                {{/IfCond}}
                            </div>
                        </div>
                        {{/each}}
                    </div>
                    {{/IfCondObj}}
                </div>
            </div>
        </div>
    </div>
</div>
<!-- Event 17 Ends -->
{{/each}}

{{#each ComponentRecordJson.zig-zag-right-image}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding lt1-widget 
            {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} lt223-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} lt223-widget--secondary-bg {{/IfCond}} {{/each}}
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
            ">

  {{IfCond data.section-title 'notempty' ''}}
  <h2 class="title title--xxl margin-bottom-xl" tabindex="0">{{data.section-title}}</h2>
  {{/IfCond}}

  <div class="uk-container lt1-widget__container snippet__container">
    {{#each Child.layout-1-zigzag}}
    {{IfCond this.ParentId  '='  ../this.Id}}
    <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
      <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
        <div class="snippet__media uk-position-relative">
          <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="616" height="640" tabindex="0">
        </div>
      </div>
      <div class="uk-width-expand@l uk-width-1-2@m snippet__col">

        {{IfCond data.snippet-tagline 'notempty' ''}}
        <div class="title title--tagline snippet__smalltitle" tabindex="0">{{data.snippet-tagline}}</div>
        {{/IfCond}}

        <h2 class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

        {{IfCond data.snippet-description 'notempty' ''}}
        <div class="js-texttoggle">
          <div class="snippet__desc text-editor" tabindex="0">                        
            {{{data.snippet-description}}}
            {{IfCond data.snippet-description-more 'notempty' ''}}
            <div class="js-texttoggle-content" tabindex="0" style="display: none;">
              {{{data.snippet-description-more}}}
            </div>
            {{/IfCond}}
          </div>
          {{IfCond data.snippet-description-more 'notempty' ''}}
          <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
            <span class="button-readmore__text button-readmore__text--more">Read More</span>
            <span class="button-readmore__text button-readmore__text--less">Less</span>
          </button>
          {{/IfCond}}
        </div>
        {{/IfCond}}


        {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
        <div class="snippet__actions"> 

          {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
          <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">{{data.snippet-primary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
          {{/IfCond}}  

          {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
          <a href="{{data.snippet-secondary-button-link}}" class="uk-button button-link ">
            {{IfCond data.snippet-secondary-button-icon 'notempty' ''}} 
            <span class="template-icon uk-margin-small-right {{data.snippet-secondary-button-icon}}"></span>
            {{/IfCond}} 
            {{data.snippet-secondary-button-text}}
            <span class="sr-only">for {{data.snippet-title}}</span>
          </a>
          {{/IfCond}}
        </div>
        {{/IfMultipleOrCond}}
      </div>
    </div>
    {{/IfCond}}
    {{/each}}
  </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.section-layout-with-dots-slider}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding lt212slider-widget js-lt212slider-widget  
        {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} lt212-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} lt212-widget--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
    ">


    <div class="uk-container lt212slider-widget__container snippet__container dynamiccomponenteditenable" %%componentRecordEditable%%>

    {{IfCond data.section-title 'notempty' ''}}
        <h2 class="title title--xl margin-bottom-xl lt212slider-widget__title js-lt212slider-widget-main-title" tabindex="0">{{data.section-title}}</h2>
    {{/IfCond}}

        <div class="snippet uk-slider uk-slider--fade uk-position-relative lt212slider-widget__slider" data-uk-slider="clsActivated: uk-transition-active; velocity: 10">

            <div class="uk-slider-container">
                <div class="uk-slider-items uk-child-width-1-1">

                    {{#each Child.section-layout-with-dots-slider-items}}
                        {{IfCond this.ParentId  '='  ../this.Id}}
                        <div class="uk-transition-fade lt212slider-widget__item dynamiccomponenteditenable" %%componentRecordEditable%%>
                            <div class="uk-grid gutter-xl lt212slider-widget__grid" data-uk-grid>
                                <div class="uk-width-auto@l uk-width-1-2@m snippet__col lt212slider-widget__col lt212slider-widget__col--media">
                                    <div class="snippet__media uk-position-relative">
                                        <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="616" height="640" tabindex="0">
                                    </div>
                                </div>
                                <div class="uk-width-expand@l uk-width-1-2@m snippet__col lt212slider-widget__col  lt212slider-widget__col--content js-lt212slider-widget-content-ele">

                                    {{IfCond data.snippet-tagline 'notempty' ''}}
                                        <div class="title title--tagline snippet__smalltitle" tabindex="0">{{data.snippet-tagline}}</div>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-title 'notempty' ''}}
                                    <h2 class="title title--l snippet__title" tabindex="0">{{data.snippet-title}}</h2>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-sub-title 'notempty' ''}}
                                    <h2 class="title title--s snippet__title" tabindex="0">{{data.snippet-sub-title }}</h2>
                                    {{/IfCond}}

                                    {{IfCond data.snippet-description 'notempty' ''}}
                                    <div class="js-texttoggle">
                                        <div class="snippet__desc text-editor" tabindex="0">                        
                                            {{{data.snippet-description}}}
                                            {{IfCond data.snippet-description-more 'notempty' ''}}
                                                <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                                                    {{{data.snippet-description-more}}}
                                                </div>
                                            {{/IfCond}}
                                        </div>
                                        {{IfCond data.snippet-description-more 'notempty' ''}}
                                            <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
                                                <span class="button-readmore__text button-readmore__text--more">Read More</span>
                                                <span class="button-readmore__text button-readmore__text--less">Less</span>
                                            </button>
                                        {{/IfCond}}
                                    </div>
                                    {{/IfCond}}

                                    {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
                                        <div class="snippet__actions">                        
                                            {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
