format.formatContent:
{{#each ComponentRecordJson.home-explore}}
<section class="home-explore">
  <div class="uk-container dynamiccomponenteditenable" %%componentRecordEditable%%>
    <div class="home-explore-content">
      {{IfCond data.link 'notempty' ''}}
      <a href="{{data.link}}" class="title title--xxl title-with-cursive link-title home-explore__heading">
        <span class="animateWord">{{data.cursive-title}}</span>
        {{data.title}}
      </a>
      {{else}}
      {{IfCond data.title 'notempty' ''}}
      <h2 class="title title--xxl title-with-cursive home-explore__heading" tabindex="0">
        {{IfCond data.cursive-title 'notempty' ''}}
        <span class="animateWord">{{data.cursive-title}}</span>
        {{/IfCond}}
        {{data.title}}
      </h2>
      {{/IfCond}}
      {{/IfCond}}
    </div>


    <div class="home__explore-plate">
      <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.image-alt}}" class="home-explore__image" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" tabindex="0"  usemap="#map" class="mapinteractive" >
      <img src="{{data.mob-image.0.OriginalImagePath}}" data-src="{{data.mob-image.0.OriginalImagePath}}" alt="{{data.image-alt}}" class="home-explore__image-mob" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" tabindex="0">

      <div style="display:none" class="pinpopup">
        <map name="map" id="map">
          {{#each childRecords}}

          <area tabindex="-1" class="marker pin icon1" shape="rect" coords="{{data.map-coords}}" data-mapcatsub="{{this.ParentId}}" data-img="{{data.main-category-name}}" href="javascript:;" data-id="{{Incremented @index}}" alt="main-category-name{{Incremented @index}}" / >

          {{/each}}
        </map>
        {{#each childRecords}}
        <div class="resort-map-desk resort-map-icon popup art-snippet newresortmap dynamiccomponenteditenable hotel_content" %%componentRecordEditable%%  data-index="{{Incremented @index}}"> 
          <div class="attraction-card " >
            <div class="art-snp-box deskmap mobmap" tabindex="0">

              <div class="art-snp-inner" data-uk-accordion > 

                <div class="art-snp-list" tabindex="0">
                  <a class="uk-accordion-title art-snp-title" href="#">
                    <h3 tabindex="0" class="title title--m dynamiccomponenteditenable" %%componentRecordEditable%%>{{data.map-title}}</h3></a>



                  <div class="card-contentwrapper" tabindex="0">
                    <div class="card-content">

                      {{IfCond data.map-title 'notempty' ''}}
                      <h3 tabindex="0" class="dynamiccomponenteditenable heading" %%componentRecordEditable%%>{{data.map-title}}</h3>
                      {{/IfCond}}
                      {{IfCond data.attraction-description 'notempty' ''}}
                      <div class="map-text" tabindex="0">{{{data.attraction-description}}}</div>
                      {{/IfCond}}
                      {{IfCond data.secondary-button-url 'notempty' ''}}
                      <a href="{{data.secondary-button-url}}" class="uk-button button-link">                        <span aria-hidden="true">{{data.secondary-button-text}}</span> 
                        <span class="sr-only">{{data.secondary-button-text}} {{data.map-title}}</span>
                      </a> 
                      {{/IfCond}} 
                    </div>
                    <div class="art-snp-img"> 
                      <img src="{{data.attraction-image.0.OriginalImagePath}}" alt="{{data.attraction-image-alt-text}}" class="img_artmap_left card-image" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" data-nodeferimage="true" tabindex="0">
                    </div>
                  </div>
                </div>

              </div>

            </div>
          </div></div>
        {{/each}}   
        <div class="resort-map-mobile resort-map-icon popup art-snippet newresortmap dynamiccomponenteditenable " %%componentRecordEditable%%  > 
          <div class="attraction-card hotel_content" data-index="{{Incremented @index}}">
            <div class="art-snp-box deskmap mobmap" >

              <div class="art-snp-inner" data-uk-accordion > 
                {{#each childRecords}}
                <div class="art-snp-list" tabindex="0">
                  <a class="uk-accordion-title art-snp-title" href="#">
                    <h3 tabindex="0" class="title title--m dynamiccomponenteditenable" %%componentRecordEditable%%>{{data.map-title}}</h3></a>



                  <div class="card-contentwrapper" tabindex="0">
                    <div class="card-content">

                      {{IfCond data.map-title 'notempty' ''}}
                      <h3 tabindex="0" class="dynamiccomponenteditenable heading" %%componentRecordEditable%%>{{data.map-title}}</h3>
                      {{/IfCond}}
                      {{IfCond data.attraction-description 'notempty' ''}}
                      <div class="map-text" tabindex="0">{{{data.attraction-description}}}</div>
                      {{/IfCond}}
                      {{IfCond data.secondary-button-url 'notempty' ''}}
                      <a href="{{data.secondary-button-url}}" class="uk-button button-link">                        <span aria-hidden="true">{{data.secondary-button-text}}</span> 
                        <span class="sr-only">{{data.secondary-button-text}} {{data.map-title}}</span>
                      </a> 
                      {{/IfCond}} 
                    </div>
                    <div class="art-snp-img"> 
                      <img src="{{data.attraction-image.0.OriginalImagePath}}" alt="{{data.attraction-image-alt-text}}" class="img_artmap_left card-image" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}" data-nodeferimage="true" tabindex="0">
                    </div>
                  </div>
                </div>
                {{/each}}  
              </div>

            </div>
          </div></div>


      </div>
    </div>
  </div>
</section>

{{/each}}

{{#each ComponentRecordJson.page-loader-overlay}}
<div class="loader-wrapper">
  <div class="loader"></div>
</div>
{{/each}}

{{#each ComponentRecordJson.faqs-with-left-title}}
<!-- FAQ 3 Starts -->
<div class="faq3-widget {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} faq3-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} faq3-widget--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
    <div class="uk-container faq3-widget__container">
        <div class="uk-grid faq3-widget__grid" data-uk-grid>
            <div class="uk-width-expand@l uk-width-1-3@m uk-width-2-5@s faq3-widget__col faq3-widget__col--left">

                {{IfCond data.section-tagline 'notempty' ''}}
                <div class="title title--tagline faq3-widget__smalltitle" tabindex="0">{{data.section-tagline}}</div>
                {{/IfCond}}

                <h2 class="title title--xl faq3-widget__title dynamiccomponenteditenable" %%componentRecordEditable%%
                    tabindex="0">{{data.section-title}}</h2>

                {{IfCond data.section-description 'notempty' ''}}
                <div class="text-editor faq3-widget__text" tabindex="0">{{{data.section-description}}}</div>
                {{/IfCond}}

                {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
                <div class="faq3-widget__action">
                    <a href="{{data.button-link}}" class="uk-button uk-button-primary ">{{data.button-text}} <span
                            class="sr-only"> - {{data.section-title}}</span> </a>
                </div>
                {{/IfCond}}

            </div>
            {{/each}}

            <div class="uk-width-auto@l uk-width-2-3@m uk-width-3-5@s faq3-widget__col faq3-widget__col--right">
                <div class="faq3-widget__content">
                    <div class="uk-accordion faq3-widget__accordion" data-uk-accordion>
                        {{#each FAQJson.Faqs}}
                        <div class="dynamiccomponenteditenable" %%componentRecordEditable%%>
                            <a class="uk-accordion-title" href="#">{{FaqQuestionName}}</a>
                            <div class="uk-accordion-content" tabindex="0" hidden>
                                {{{Answer}}}
                            </div>
                        </div>
                        {{/each}}
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
<!-- Event 17 Ends -->

{{#each ComponentRecordJson.feature-left-thumbnail-image}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding feature-left2 feature-left-thumbnail-image  {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
  {{IfCond data.section-title 'notempty' ''}}
  <h2 class="title title--xxl margin-bottom-xl" tabindex="0">{{data.section-title}}</h2>
  {{/IfCond}}

  <div class="uk-container feature-left2__container snippet__container">
    {{#each Child.feature-left-image-2-items}}
    {{IfCond this.ParentId  '='  ../this.Id}}
    <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
      <div class="uk-width-1-2@m snippet__col uk-position-relative">
        <div class="snippet__media feature-left2__media uk-position-relative">
          <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="467" height="610" tabindex="0">
        </div>
        <div class="snippet__media__small feature-left2__media__small">
          <img src="{{data.snippet-thumbnail-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="238" height="287" tabindex="0">
        </div>
      </div>
      <div class="uk-width-1-2@m snippet__col">

        {{IfCond data.snippet-tagline 'notempty' ''}}
        <div class="small-title" tabindex="0">{{data.snippet-tagline}}</div>
        {{/IfCond}}

        <h2 class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

        {{IfCond data.snippet-description 'notempty' ''}}
        <div class="js-texttoggle">
          <div class="snippet__desc text-editor" tabindex="0">                        
            {{{data.snippet-description}}}
            {{IfCond data.snippet-description-more 'notempty' ''}}
            <div class="js-texttoggle-content" tabindex="0" style="display: none;">
              {{{data.snippet-description-more}}}
            </div>
            {{/IfCond}}
          </div>
          {{IfCond data.snippet-description-more 'notempty' ''}}
          <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
            <span class="button-readmore__text button-readmore__text--more">Read More
              <span class="sr-only">for {{data.snippet-title}}</span>
            </span>
            <span class="button-readmore__text button-readmore__text--less">Less
              <span class="sr-only">for {{data.snippet-title}}</span>
            </span>
          </button>
          {{/IfCond}}
        </div>
        {{/IfCond}}


        {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
        <div class="snippet__actions">                        
          {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
          <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">{{data.snippet-primary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
          {{/IfCond}}                        
          {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
          <a href="{{data.snippet-secondary-button-link}}" class="uk-button uk-button-secondary">{{data.snippet-secondary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
          {{/IfCond}}
        </div>
        {{/IfMultipleOrCond}}
      </div>
    </div>
    {{/IfCond}}
    {{/each}}
  </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.feature-left-image-with-awards}}
<!-- Layout 1 Left Side image Starts-->
<div class="inner-padding feature-left-img  {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} {{IfCond data.enable-background-image[].0 '=' 'Yes'}} bg-img {{/IfCond}}">
  <div class="intro-sectionbg">
    <img src="{{data.bg-image.0.OriginalImagePath}}" 
         alt="{{data.bg-image-alt}}" 
         width="1440" height="726" tabindex="0">
  </div>




  <div class="uk-container feature-leftimg__container snippet__container">
    {{#each Child.feature-image-2-items}}
    <div class="feature-overview">
      {{IfCond this.ParentId  '='  ../this.Id}}
      <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
        <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
          <div class="snippet__media feature-left_img__media uk-position-relative">
            <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="495" height="502" tabindex="0">
          </div>
        </div>
        <div class="uk-width-expand@l uk-width-1-2@m snippet__col">

          {{IfCond data.snippet-tagline 'notempty' ''}}
          <div class="small-title" tabindex="0">{{data.snippet-tagline}}</div>
          {{/IfCond}}

          {{IfCond data.consider-as-h1[].0 '=' 'Yes'}}
          <h1 id="main-content-h1" class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h1>
          {{else}}

          <h2 class="title title--xl snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

          {{/IfCond}}

          {{IfCond data.snippet-description 'notempty' ''}}
          <div class="js-texttoggle">
            <div class="snippet__desc text-editor" tabindex="0">                        
              {{{data.snippet-description}}}
              {{IfCond data.snippet-description-more 'notempty' ''}}
              <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                {{{data.snippet-description-more}}}
              </div>
              {{/IfCond}}
            </div>
            {{IfCond data.snippet-description-more 'notempty' ''}}
            <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
              <span class="button-readmore__text button-readmore__text--more">Read More
                <span class="sr-only">for {{data.snippet-title}}</span>
              </span>
              <span class="button-readmore__text button-readmore__text--less">Less
                <span class="sr-only">for {{data.snippet-title}}</span>
              </span>
            </button>
            {{/IfCond}}
          </div>
          {{/IfCond}}


          {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
          <div class="snippet__actions">                        
            {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
            <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">{{data.snippet-primary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
            {{/IfCond}}                        
            {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
            <a href="{{data.snippet-secondary-button-link}}" class="uk-button uk-button-secondary">{{data.snippet-secondary-button-text}} <span class="sr-only">for {{data.snippet-title}}</span></a>
            {{/IfCond}}
          </div>
          {{/IfMultipleOrCond}}
        </div>
      </div>
      {{/IfCond}}
    </div>

    <!-- Awards -->
    {{IfCond data.award-section[].0 '=' 'Yes'}}
    <div class="uk-container">
      <div class="home-awards-section">
        <div class="home-awards-heading">
          {{IfCond data.award-title 'notempty' ''}}
          <h2 class="homeawards__maintitle" tabindex="0">{{data.award-title}}</h2>
          {{/IfCond}}
        </div>

        <div class="feature-award-container">
          <div class="awardssliderlist uk-position-relative uk-visible-toggle" data-name="awardsslider" data-uk-scrollspy="cls: uk-animation-fade; target: .home-awards__grid; delay: 200;" data-uk-slider>


             <div class="uk-slider-items uk-grid">
            {{#each Child.feature-award-section}}

                 <div class="uk-width-4-5 uk-width-1-5@m home-awards__grid">
              <div class="home-awards__box">
                <span class="home-awards__icon dynamiccomponenteditenable" %%componentRecordEditable%%>
                  <img src="{{data.award-logo.0.OriginalImagePath}}" 
                       alt="{{data.award-logo-alt-text}}" 
                       width="{{data.width}}" 
                       height="{{data.height}}" 
                       tabindex="0">
                </span>
              </div>
            </div>
            {{/each}}
            </div>
          </div>
        </div>
      </div>
    </div>
    {{/IfCond}}

    {{/each}} 
  </div>
</div>
<!-- Layout 1 Left Side image Ends-->
{{/each}}

{{#each ComponentRecordJson.snippet-with-small-image}}
<!-- Layout A2 - A3 Left Side image Starts -->
<div class="lt-a2-a3 snippet-with-small-image {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} lt-a2-a3-widget--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} lt223-widget--secondary-bg {{/IfCond}} {{/each}}">


  <div class="uk-container snippet__container">
    {{#each Child.snippet-with-small-image-items}}
    {{IfCond this.ParentId  '='  ../this.Id}}
    <div class="uk-grid uk-flex-middle gutter-xl snippet__zigzag {{#each ../../data.start-grid-image-from[]}} {{IfCond this '=' 'Right'}} snippet__zigzag--revert {{/IfCond}} {{/each}}" data-uk-grid>
      <div class="uk-width-auto@l uk-width-1-2@m snippet__col">
        <div class="snippet__media dynamiccomponenteditenable" %%componentRecordEditable%%>
          <img src="{{data.snippet-image.0.OriginalImagePath}}" alt="{{data.snippet-image-alt-text}}" width="{{data.snippet-image.0.ResourceWidth}}" 
               height="{{data.snippet-image.0.ResourceHeight}}" tabindex="0">
        </div>
      </div>
      <div class="uk-width-expand@l uk-width-1-2@m snippet__col">


          {{IfCond data.sub-title 'notempty' ''}}
          <div class="sub-title" tabindex="0">{{data.sub-title}}</div>
          {{/IfCond}}

          <h2 class="snippet__title dynamiccomponenteditenable" %%componentRecordEditable%% tabindex="0">{{data.snippet-title}}</h2>

          {{IfCond data.snippet-description 'notempty' ''}}
          <div class="js-texttoggle">
            <div class="snippet__desc text-editor" tabindex="0">                        
              {{{data.snippet-description}}}
              {{IfCond data.snippet-description-more 'notempty' ''}}
              <div class="js-texttoggle-content" tabindex="0" style="display: none;">
                {{{data.snippet-description-more}}}
              </div>
              {{/IfCond}}
            </div>
            {{IfCond data.snippet-description-more 'notempty' ''}}
            <button type="button" class="uk-button button-link button-readmore js-texttoggle-action" tabindex="0">
              <span class="button-readmore__text button-readmore__text--more">Read More</span>
              <span class="button-readmore__text button-readmore__text--less">Less</span>
              <span class="sr-only">for {{data.snippet-title}}</span>
            </button>
            {{/IfCond}}
          </div>
          {{/IfCond}}


          {{IfMultipleOrCond data.snippet-primary-button-link 'notempty' '' data.snippet-secondary-button-link 'notempty' ''}}     
          <div class="snippet__actions">                        
            {{IfCond data.snippet-primary-button-link 'notempty' '' 'and' data.snippet-primary-button-text 'notempty' ''}}
            <a href="{{data.snippet-primary-button-link}}" class="uk-button uk-button-primary">
              <span aria-hidden="true">{{data.snippet-primary-button-text}}  </span>             
              <span class="sr-only">{{data.snippet-primary-button-text}} for {{data.snippet-title}}</span></a>
            {{/IfCond}}                        
            {{IfCond data.snippet-secondary-button-link 'notempty' '' 'and' data.snippet-secondary-button-text 'notempty' ''}}
            <a href="{{data.snippet-secondary-button-link}}" class="uk-button uk-button-secondary">                                       
              <span aria-hidden="true">{{data.snippet-secondary-button-text}}</span>
              <span class="sr-only">{{data.snippet-secondary-button-text}} for {{data.snippet-title}}</span>
            </a>
            {{/IfCond}}
          </div>
          {{/IfMultipleOrCond}}

      </div>

    </div>
    {{/IfCond}}
    {{/each}}
  </div>
</div>
<!-- Layout A2 - A3 Left Side image Ends -->
{{/each}}

{{#each ComponentRecordJson.3-col-grid-card}}
<div class="inner-padding lt229-widget grid-card
            {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
            {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}">
  <div class="uk-container dynamiccomponenteditenable" %%componentRecordEditable%%>

    <div class="uk-text-center is-inverse snippet-grid__head--l12">
      {{IfCond data.section-tag-line 'notempty' ''}}
      <div class="small-title" tabindex="0">{{data.section-tag-line}}</div>
       {{/IfCond}}

    {{IfCond data.title 'notempty' ''}}
    <div class="snippet-grid__head">
      <h2 class="title title--xl lt229-widget__maintitle" tabindex="0">{{data.title}}</h2>
    </div>
    {{/IfCond}}
      </div>
    <div class="lt229-widget__slider">
      <div class="uk-grid snippet-grid" data-uk-grid>
        {{#each Child.3-col-grid-card-child}}
        <div class="lt229-widget__box grid-card uk-width-1-3@m uk-width-1-2@s">
          <div class="snippet-grid__box">
            <div class="snippet-grid__media snippet-grid__media--overlay ">
              <img src="{{data.image.0.OriginalImagePath}}" alt="{{data.alt-text}}" width="{{data.image.0.ResourceWidth}}" height="{{data.image.0.ResourceHeight}}"  tabindex="0">
            </div>
            <div class="snippet-grid__content dynamiccomponenteditenable three-col-snippet-grid__content" %%componentRecordEditable%%>
              <h3 class="title title--s lt229-widget__title is-inverse uk-position-smal uk-position-bottom" tabindex="0">{{data.title}}</h3>
              {{IfCond data.description 'notempty' ''}}
              <div class="lt229-widget__desc text-editor" tabindex="0">
                {{{data.description}}}
              </div>
              {{/IfCond}}
              {{IfCond data.secondary-button-text 'notempty' '' 'and' data.secondary-button-link 'notempty' ''}}
              <div class="three-col-snippet-grid__actions"> 
                <a href="{{data.secondary-button-link}}"
                   class="uk-button button-link">{{data.secondary-button-text}} 
                  <span class="sr-only"> about - {{data.title}}</span></a>

              </div>
              {{/IfCond}}

            </div>
          </div>
        </div>
        {{/each}}
      </div>
    </div>
  </div>
</div>
{{/each}}

{{#each ComponentRecordJson.home-explore}}
<section class="home-explore">
  <div class="uk-container dynamiccomponenteditenable" %%componentRecordEditable%%>
    <div class="home-explore-content">
      {{IfCond data.link 'notempty' ''}}
      <a href="{{data.link}}" class="title title--xxl title-with-cursive link-title home-explore__heading">
        <span class="animateWord">{{data.cursive-title}}</span>
        {{data.title}}
      </a>
      {{else}}
      {{IfCond data.title 'notempty' ''}}
      <h2 class="title title--xxl title-with-cursive home-explore__heading" tabindex="0">
        {{IfCond data.cursive-title 'notempty' ''}}
        <span class="animateWord">{{data.cursive-title}}</span>
        {{/IfCond}}
        {{data.title}}
      </h2>
      {{/IfCond}}
      {{/IfCond}}
    </div>


