{{#each ComponentRecordJson.mahogany-header}}
<header class="header" id="header">

    <div class="uk-container header__container">
        <div class="uk-grid header__grid" data-uk-grid>
            <div class="uk-width-auto header__col header__col--left">

                {{IfCondObj Child.mahogany-header-main-navigation '>' '0'  }}
                <div class="header__toggler">
                    <button type="button" class="header__toggler-btn js-header-toggler">
                        <span class="template-icon template-icon-menu-03 is-togglemenu-close"></span>
                        <span class="template-icon template-icon-x-close is-togglemenu-active"></span>
                        <span class="sr-only">Toggle Main Navigation</span>
                    </button>
                </div>
                {{/IfCondObj}}

                <div class="header-mainmenu">
                    {{#each Child.mahogany-header-main-navigation}}
                        {{IfCond this.ParentId  '='  ../this.Id}}
                            {{IfCond data.enable-link-in-top-navigation[].0 '=' 'Yes'}}
                                <div class="header-mainmenu__item">
                                    <a href="{{data.menu-item-link}}" {{IfCond data.open-url-in-new-tab[].0 '=' 'Yes'}}target="_blank"{{/IfCond}} class="header-mainmenu__link nav">{{data.menu-item-name}}</a>
                                </div>
                            {{/IfCond}}
                        {{/IfCond}}
                    {{/each}}
                </div>

                <div class="header-collapsible navmenu js-header-collapse">
                    <div class="header-collapsible__content navmenu" id="navmenu">
                        <div class="header-collapsible__scroll">

                            <ul class="navmenu__list">

                                {{#each Child.mahogany-header-main-navigation}}
                                    {{IfCond this.ParentId  '='  ../this.Id}}
                                        {{IfCond data.enable-dropdown[].0 '=' 'Yes'}} <!-- If Dropdown menu exists -->
                                            <li class="navmenu__list-item navmenu__dropdown submenulist js-navmenu-dropmenu-wrapper dynamiccomponenteditenable" %%componentRecordEditable%%> 
                                                <a href="javascript:;" class="navmenu__list-link nav js-navmenu-dropmenu-trigger">{{data.menu-item-name}}</a>
                                                <ul class="uk-dropdown uk-drop navmenu-dropmenu js-navmenu-dropmenu" data-uk-dropdown>
                                                    {{#each Child.mahogany-header-main-navigation-dropdown}}
                                                        {{IfCond this.ParentId  '='  ../this.Id}}
                                                            <li class="navmenu-dropmenu__item dynamiccomponenteditenable" %%componentRecordEditable%%>
                                                                <a href="{{data.dropdown-menu-item-link}}" {{IfCond data.open-url-in-new-tab[].0 '=' 'Yes'}}target="_blank"{{/IfCond}} class="navmenu-dropmenu__link nav"> {{data.dropdown-menu-item-name}} </a> 
                                                            </li>
                                                        {{/IfCond}}
                                                    {{/each}}
                                                </ul>
                                            </li>
                                        {{else}}
                                            <li class="navmenu__list-item {{IfCond data.menu-item-name '=' 'Home'}} navmenu__list-item--onlymob {{/IfCond}} dynamiccomponenteditenable" %%componentRecordEditable%%>
                                                <a href="{{data.menu-item-link}}" {{IfCond data.open-url-in-new-tab[].0 '=' 'Yes'}}target="_blank"{{/IfCond}} class="navmenu__list-link nav">{{data.menu-item-name}}</a>
                                            </li>
                                        {{/IfCond}}
                                    {{/IfCond}}
                                {{/each}}
                            </ul>

                            %%SocialMediaLink_mahogany-social-media%%
                        </div>
                    </div>
                    <div class="header-collapsible__media">
                        <img src="{{data.toggle-menu-banner-image.0.OriginalImagePath}}" alt="{{data.toggle-menu-banner-image-alt-text}}" tabindex="0">
                    </div>
                </div>                            
            </div>
            <div class="uk-width-auto header__col header__col--center">
                <a href="%%strpath%%" class="header__logo">
                    <img src="{{data.main-logo.0.OriginalImagePath}}" class="header__logo-img" data-uk-svg alt="{{data.main-logo-alt-text}}" width="156" height="43" data-nodeferimage="true">
                    <span class="sr-only">{{data.main-logo-alt-text}}</span>
                </a>
            </div>

            <div class="uk-width-auto header__col header__col--right">
                {{IfCond data.get-phone-number-from-mpc[].0 '=' 'Yes'}}
                <div class="header__phone">
                    {{IfCond data.phone-no-label 'notempty' ''}}<span class="header__phone-label">Call:</span>{{/IfCond}}
                    <a href="tel:%%phone1%%" class="header__phone-text">
                        <span class="template-icon template-icon-phone"></span> <span class="header__phone-no"> %%phone1%%</span>
                    </a>
                </div>
                {{else}}
                <div class="header__phone">
                    {{IfCond data.phone-no-label 'notempty' ''}}<span class="header__phone-label">Call:</span>{{/IfCond}}
                    <a href="{{data.phone-no-link}}" class="header__phone-text">
                        <span class="template-icon template-icon-phone"></span> <span class="header__phone-no"> {{data.phone-no}}</span></a>
                </div>
                {{/IfCond}}

                {{IfCond data.enable-button-to-open-booking-widget[].0 '=' 'Yes'}}
                    <a href="#booking-widget-modal" role="button" class="uk-button uk-button-primary header__bookstay" data-uk-toggle>{{data.button-text}}</a>
                {{else}}
                    {{IfCond data.button-link 'notempty' '' 'and' data.button-text 'notempty' ''}}
                        <a href="{{data.button-link}}" class="uk-button uk-button-primary header__bookstay">{{data.button-text}}</a>
                    {{/IfCond}}
                {{/IfCond}}
            </div>
        </div>
    </div>

</header>
{{/each}}

