                        </div>
                    {{/IfCond}}

                    {{IfCond data.author-name 'notempty' ''}}
                        <h3 class="title testimonial-widget__name" tabindex="0">{{data.author-name}}</h3>
                    {{/IfCond}}
                </div>
                {{/each}}

            </div>

            {{IfCondObj Child.testimonial-slider-items '>' '1'  }}


          		{{IfCond data.display-navigation-arrows[].0 '=' 'Yes'}}
