{{#each ComponentRecordJson.history-timeline-with-slider}}
<div class="timeline-slider uk-slider {{#each data.change-background-color[]}}{{IfCond this '=' 'Primary Color'}} timeline-slider--primary-bg {{/IfCond}} {{IfCond this '=' 'Secondary Color'}} timeline-slider--secondary-bg {{/IfCond}} {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}}"  data-uk-slider>

    <div class="uk-container">

        {{IfMultipleOrCond data.section-tagline 'notempty' '' data.section-title 'notempty' ''data.section-description 'notempty' ''}}  
            <div class="snippet-grid__head dynamiccomponenteditenable" %%componentRecordEditable%%>
                {{IfCond data.section-tagline 'notempty' ''}}
                    <div class="title title--tagline snippet-grid__maintagline" tabindex="0">{{data.section-tagline}}</div>
                {{/IfCond}}

                {{IfCond data.section-title 'notempty' ''}}
                    <h2 class="title title--xl snippet-grid__maintitle" tabindex="0">{{data.section-title}}</h2>
                {{/IfCond}}

                {{IfCond data.section-description 'notempty' ''}}
                    <div class="text-editor snippet-grid__maindesc" tabindex="0">
                        {{{data.section-description}}}
                    </div>
                {{/IfCond}}

                {{IfMultipleOrCond data.primary-button-link 'notempty' '' data.secondary-button-link 'notempty' ''}}  
                    <div class="button-combo  snippet-grid__mainactions">
                        {{IfCond data.primary-button-link 'notempty' '' 'and' data.primary-button-text 'notempty' ''}}
                            <a href="{{data.primary-button-link}}" class="uk-button uk-button-primary">{{data.primary-button-text}} <span class="sr-only">{{data.section-title}}</span> </a>
                        {{/IfCond}}                        
                        {{IfCond data.secondary-button-link 'notempty' '' 'and' data.secondary-button-text 'notempty' ''}}
                            <a href="{{data.secondary-button-link}}" class="uk-button button-link">{{data.secondary-button-text}} <span class="sr-only">{{data.section-title}}</span> </a>
                        {{/IfCond}}
                    </div>
                {{/IfMultipleOrCond}}
            </div>
        {{/IfMultipleOrCond}}

        <div class="uk-slider-container uk-position-relative">
            <div class="uk-slider-items uk-grid snippet-grid timeline-slider__grid" data-uk-grid>

                {{#each Child.items-history-timeline-with-slider}}
                    {{IfCond this.ParentId  '='  ../this.Id}}
                        <div class="uk-width-1-3@m uk-width-1-2@s">
                            <div class="snippet-grid__box timeline-slider__box dynamiccomponenteditenable" %%componentRecordEditable%%>

                                <div class="timeline-slider__media">
                                  <div class="snippet-grid__media">
                                      <img src="{{data.image.0.OriginalImagePath}}" width="256" height="256" alt="{{data.alt-text}}" tabindex="0">
                                  </div>
                                </div>

                                <div class="snippet-grid__content timeline-slider__content">

                                    {{IfCond data.title 'notempty' ''}}
                                        <h3 class="title title--s snippet-grid__title" tabindex="0">{{data.title}}</h3>
