                                    <a class="nav" href="{{data.sub-navigation-item-link}}" {{IfCond data.open-link-in-a-new-tab[].0 '=' 'Yes'}}target="_blank"{{/IfCond}}>{{data.sub-navigation-item-name}}</a>
                                </div>
                            {{/IfCond}}
                        {{/each}}
                    </div>
                </div>
            {{/IfCondObj}}
        </div>

    </div>
</footer>
{{/each}}

{{#each ComponentRecordJson.review-slider}}
<!-- Testimonial 7 Starts -->
<div class="tml7-widget 
        {{#each data.change-background-color[]}}
            {{IfCond this '=' 'Primary Color'}} tml7-widget--primary-bg {{/IfCond}} 
            {{IfCond this '=' 'Secondary Color'}} tml7-widget--secondary-bg {{/IfCond}} 
            {{IfCond this '=' 'Tertiary Color'}} tml7-widget--tertiary-bg{{/IfCond}} 
            {{IfCond this '=' 'Gradient Color'}} tml7-widget--gradient-bg {{/IfCond}} 
        {{/each}}
        {{IfCond data.set-top-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-top {{/IfCond}} 
        {{IfCond data.set-bottom-padding-to-zero[].0 '=' 'Yes'}} uk-padding-remove-bottom {{/IfCond}} 
    ">
    <div class="uk-container tml7-widget__container">


        {{IfCond data.section-logo-image 'notempty' '' 'and' data.section-logo-image-alt-text 'notempty' ''}}
        <div class="tml7-widget__logo">
            <img src="{{data.section-logo-image.0.OriginalImagePath}}" width="62" height="25" alt="{{data.section-logo-image-alt-text}}" tabindex="0">
        </div>
        {{/IfCond}}


        {{IfCondObj Child.review-slider-items '>' '0'  }}
        <div class="uk-position-relative uk-slider uk-slider--fade uk-slider-container tml7-widget__slider" tabindex="-1" data-uk-slider="clsActivated: uk-transition-active; velocity: 10">

            <div class="uk-slider-items uk-child-width-1-1">

                {{#each Child.review-slider-items}}
                <div class="tml7-widget__item uk-transition-fade {{IfCond @index '=' 0}} uk-active uk-transition-active {{/IfCond}} dynamiccomponenteditenable" %%componentRecordEditable%%>

                    {{IfCond data.review-logo-image 'notempty' '' 'and' data.review-logo-image-alt-text 'notempty' ''}}
                        <div class="tml7-widget__logo">
                            <img src="{{data.review-logo-image.0.OriginalImagePath}}" width="62" height="25" alt="{{data.review-logo-image-alt-text}}" tabindex="0">
                        </div>
                    {{/IfCond}}

                    {{IfCond data.review-text 'notempty' ''}}
                        <div class="tml7-widget__text title title--m" tabindex="0">
                            {{{data.review-text}}}
                        </div>
                    {{/IfCond}}

                    {{IfCond data.author-name 'notempty' ''}}
                        <h3 class="title tml7-widget__name" tabindex="0">{{data.author-name}}</h3>
                    {{/IfCond}}
                </div>
                {{/each}}

            </div>

            {{IfCondObj Child.review-slider-items '>' '1'  }}

                <div class="slider-controls">
                    <div class="slider-controls__arrows uk-flex uk-flex-middle uk-flex-between">
                        <a class="slider-controls__slidenav slider-controls__slidenav--left template-icon-left-arrow uk-icon uk-slidenav-previous uk-slidenav" href="#" data-uk-slider-item="previous">
                            <span class="template-icon template-icon-arrow-left"></span>
                            <span class="sr-only">Go to Previous Slide of {{data.section-title}}</span>
                        </a>

                        <a class="slider-controls__slidenav slider-controls__slidenav--right template-icon-right-arrow uk-icon uk-slidenav-next uk-slidenav" href="#" data-uk-slider-item="next">
                            <span class="template-icon template-icon-arrow-right"></span>
                            <span class="sr-only">Go to Next Slide of {{data.section-title}}</span>
                        </a>
                    </div>
                </div>

                <ul class="uk-slider-nav uk-dotnav uk-flex-center tml7-widget__dotnav">
                    {{#each Child.review-slider-items}}
                    <li data-uk-slider-item="{{@index}}" class="{{IfCond @index '=' 0}}uk-active{{/IfCond}}">
                        <a href="javascript:;"><span class="sr-only">Go to slide {{Incremented @index}} - {{data.author-name}}</span></a>
                    </li>
                    {{/each}}
                </ul>

            {{/IfCondObj}}

        </div>
        {{/IfCondObj}}
    </div>
</div>
<!-- Testimonial 7 Ends -->
{{/each}}

